Hmm_Train_V0_1_0 = CommandToolBuilder(tool="hmm_train", base_command=["hmm_train"], inputs=[ToolInput(tag="in_list_multiple_filescurrently", input_type=Int(optional=True), prefix="-m", doc=InputDocumentation(doc="List of multiple sequence alignment files.\nCurrently, in testing mode, the list must be of length one.")), ToolInput(tag="in_file_defining_mapping", input_type=File(optional=True), prefix="-c", doc=InputDocumentation(doc="File defining mapping of feature types to category\nnumbers.")), ToolInput(tag="in_files_gff_defining", input_type=Int(optional=True), prefix="-g", doc=InputDocumentation(doc="Files in GFF defining sequence\nfeatures to be used in labeling sites.   Frame of reference of\nfeature indices is determined feature-by-feature according to\n'seqname' attribute.  Filenames must correspond in number and order\nto the elements of <msa_fname_list>.")), ToolInput(tag="in_mutually_exclusive_m", input_type=Int(optional=True), prefix="-M", doc=InputDocumentation(doc="(Mutually exclusive with -m) Assume alignments\nof the specified lengths (comma-separated list) and do not not\nattempt to map the coordinates in the specified GFFs (assume\nthey are in the desired coordinate frame).  This option allows\nan HMM to be trained directly from GFFs, without alignments.\nNot permitted with -I.")), ToolInput(tag="in_fastampmssdefault_ss_alignment", input_type=String(optional=True), prefix="-i", doc=InputDocumentation(doc="|FASTA|MPM|SS\n(default SS) Alignment format.")), ToolInput(tag="in_estimating_group_features", input_type=String(optional=True), prefix="-R", doc=InputDocumentation(doc="Before estimating transition probabilities, group features by <tag>\n(e.g., 'transcript_id' or 'exon_id') and reverse complement\nsegments of the alignment corresponding to groups on the\nreverse strand.  Groups must be non-overlapping (see refeature\n--unique).")), ToolInput(tag="in_model_indels_specified", input_type=Int(optional=True), prefix="-I", doc=InputDocumentation(doc="Model indels for specified categories.  To have\nnonzero probability for the states corresponding to a\nspecified category range, indels must be 'clean'\n(nonoverlapping), must be assignable by parsimony to a single\nbranch in the phylogenetic tree, and must have lengths that\nare exact multiples of the category range size.  Avoid -G with\nthis option.  If used in training mode, requires -T.")), ToolInput(tag="in_use_specified_topology", input_type=String(optional=True), prefix="-t", doc=InputDocumentation(doc="Use the specified tree topology when training\nfor indels.")), ToolInput(tag="in_train_indel_model", input_type=Int(optional=True), prefix="-n", doc=InputDocumentation(doc="Train an indel model for <nseqs>\nsequences, despite that the training alignment has a different\nnumber.  All (non-trivial) gap patterns are assumed to be\nequally frequent.")), ToolInput(tag="in_proceed_quietly_stderr", input_type=Boolean(optional=True), prefix="-q", doc=InputDocumentation(doc="Proceed quietly (without updates to stderr)."))], outputs=[], container=None, version="v0.1.0")


