Msa_View_V0_1_0 = CommandToolBuilder(tool="msa_view", base_command=["msa_view"], inputs=[ToolInput(tag="in_out_format", input_type=File(optional=True), prefix="--out-format", doc=InputDocumentation(doc="|FASTA|MPM|SS\n(Default FASTA)  Output file format.")), ToolInput(tag="in_tuple_size", input_type=Int(optional=True), prefix="--tuple-size", doc=InputDocumentation(doc="(For use with --out-format SS).  Represent an alignment in\nterms of tuples of columns of the designated size.  Useful\nwith context-dependent phylogenetic models.")), ToolInput(tag="in_start", input_type=Int(optional=True), prefix="--start", doc=InputDocumentation(doc="Starting column of sub-alignment (indexing starts with 1).\nDefault is 1.  Note that coordinates use the frame of reference\nof the entire alignment unless --refidx 1 is specified.")), ToolInput(tag="in_end", input_type=Int(optional=True), prefix="--end", doc=InputDocumentation(doc="Ending column of sub-alignment.  Default is length of\nalignment.  Note that coordinates use the frame of reference\nof the entire alignment unless --refidx 1 is specified.")), ToolInput(tag="in_seqs", input_type=Int(optional=True), prefix="--seqs", doc=InputDocumentation(doc="Comma-separated list of sequences to include (default)\nexclude (if --exclude).  Indicate by sequence number or name\n(numbering starts with 1 and is evaluated *after* --order is\napplied).")), ToolInput(tag="in_exclude", input_type=Boolean(optional=True), prefix="--exclude", doc=InputDocumentation(doc="Exclude rather than include specified sequences.")), ToolInput(tag="in_ref_idx", input_type=Int(optional=True), prefix="--refidx", doc=InputDocumentation(doc="Index of reference sequence for coordinates.  Use 0 to\nindicate the coordinate system of the alignment as a whole\n(this is the default).")), ToolInput(tag="in_aggregate", input_type=File(optional=True), prefix="--aggregate", doc=InputDocumentation(doc="(Not compatible with --start or --end) Create an aggregate\nalignment from a set of alignment files, by concatenating\nindividual alignments.  If used with --out-format SS and\n--unordered-ss, the aggregate alignment will never be created\nexplicitly (recommended for large data sets).  The argument\n<name_list> must be a list of sequence names, including all\nnames in all specified alignments (missing sequences will be\nreplaced by rows of missing data).  The standard <msa_fname>\nargument should be replaced with a list of (whitespace-\nseparated) file names.")), ToolInput(tag="in_split_all", input_type=File(optional=True), prefix="--split-all", doc=InputDocumentation(doc="Split output alignment into separate fasta files by species.\nFile naming convention is filename_root.species.fa. If used with\n--gap-strip, gap characters will be stripped from all output files.\nIn this case, '--gap-strip <s>' should NOT be used (ALL or ANY\nshould both work fine).")), ToolInput(tag="in_in_format", input_type=File(optional=True), prefix="--in-format", doc=InputDocumentation(doc="|FASTA|MPM|MAF|SS\n(Default is to guess format from file contents).  Input file\nformat.  FASTA is as usual.  PHYLIP is compatible with the formats\nused in the PHYLIP and PAML packages.  MPM is the format used by the\nMultiPipMaker aligner and some other of Webb Miller's older tools.\nMAF ('Multiple Alignment Format') is used by MULTIZ/TBA and the\nUCSC Genome Browser.  SS is a simple format describing the\nsufficient statistics for phylogenetic inference (distinct columns\nor tuple of columns and their counts).  Use --out-format SS with\n--in-format MAF for best efficiency (explicit alignment is\nnever created).  Also, use --unordered-ss if possible.")), ToolInput(tag="in_alphabet", input_type=String(optional=True), prefix="--alphabet", doc=InputDocumentation(doc="Use the specified alphabet (default 'ACGT').  In addition,\n'-' characters are assumed to represent alignment gaps, and\n'*' and 'N' characters are allowed for missing data.\nAlphabetical letters not in the alphabet will be converted to\n'N's upon input.  This option is ignored with SS input (alphabet\nspecified within SS files.)")), ToolInput(tag="in_soft_masked", input_type=Boolean(optional=True), prefix="--soft-masked", doc=InputDocumentation(doc="Implies --alphabet 'ACGTNacgtn', useful for soft-masked sequences.")), ToolInput(tag="in_unmask", input_type=Boolean(optional=True), prefix="--unmask", doc=InputDocumentation(doc="Remove soft-masking; convert to uppercase.")), ToolInput(tag="in_pretty", input_type=Boolean(optional=True), prefix="--pretty", doc=InputDocumentation(doc="Pretty-print alignment (use '.' when character matches\ncorresponding character in first sequence).  Ignored if\n--out-format SS is selected.")), ToolInput(tag="in_gap_strip", input_type=String(optional=True), prefix="--gap-strip", doc=InputDocumentation(doc="|ANY|<s>\nStrip columns containing all gaps, any gaps, or a gap in the\nspecified sequence (<s>).  Indexing starts at one and refers\nto the list *after* any sequences have been added or\nsubtracted (via --seqs and --exclude or --order).")), ToolInput(tag="in_collapse_missing", input_type=Boolean(optional=True), prefix="--collapse-missing", doc=InputDocumentation(doc="(For use with -o SS) Convert all missing-data characters and\ngaps to '*' characters.  Can be used to make sufficient\nstatistics more compact, which can improve the performance of\nphyloFit (all missing data and gap characters are typically\ntreated the same by phyloFit anyway).")), ToolInput(tag="in_mark_missing", input_type=Int(optional=True), prefix="--mark-missing", doc=InputDocumentation(doc="Convert all gaps of length greater than <maxlen> to '*'\ncharacters.  If --refidx is specified (with a positive index),\ngaps in the designated reference sequence will not be altered.\nThis is a useful heuristic for distinguishing between\nmicroindels and regions of missing data (e.g., due to\nlarge-scale indels, incomplete assemblies, or highly\ndiverged sequences).")), ToolInput(tag="in_missing_as_indels", input_type=Boolean(optional=True), prefix="--missing-as-indels", doc=InputDocumentation(doc="Convert all missing data characters (Ns and *s) to gap\ncharacters, except for Ns in a reference sequence specified by\n--refidx, which will be replaced by randomly selected\nnucleotides.  (This allows the coordinate frame for the\nreference sequence to be maintained; this option is only\nrecommended if such Ns are rare.)  If --refidx is not\nused, all Ns will be replaced by gaps.  You may want to use\n--gap-strip ALL with this option.")), ToolInput(tag="in_order", input_type=String(optional=True), prefix="--order", doc=InputDocumentation(doc="Change order of rows in alignment to match sequence names\nspecified in name_list.  If a name appears in name_list but\nnot in the alignment, a row of gaps will be inserted.  This\noption is applied to the alignment *before* --seqs,\n--refidx, and --gap-strip are applied.")), ToolInput(tag="in_reverse_complement", input_type=Boolean(optional=True), prefix="--reverse-complement", doc=InputDocumentation(doc="Reverse complement output alignment.")), ToolInput(tag="in_randomize", input_type=Boolean(optional=True), prefix="--randomize", doc=InputDocumentation(doc="Randomly permute the columns of the source alignment (done\n*before* taking sub-alignment).  Requires an ordered\nrepresentation of the alignment (careful using with\n--in-format SS|MAF -- will create full alignment from\nsufficient statistics).")), ToolInput(tag="in_fill_ns", input_type=Boolean(optional=True), prefix="--fill-Ns", doc=InputDocumentation(doc="<s:b-e>\nFill sequence no. <s> with Ns, from <b> to <e>. Applied before\n--start, --end, --seqs, --gap-strip, but after --order.\nCoordinate frame depends on --refidx.  Can be used\nmultiple times.")), ToolInput(tag="in_summary_only", input_type=Boolean(optional=True), prefix="--summary-only", doc=InputDocumentation(doc="Report only summary statistics, rather than complete\nalignment.  Statistics are for alignment that would otherwise\nbe output (i.e., after other options have been applied).")), ToolInput(tag="in_window_summary", input_type=Int(optional=True), prefix="--window-summary", doc=InputDocumentation(doc="Like -S, but output summary statistics for non-overlapping\nwindows of the specified size.")), ToolInput(tag="in_unordered_ss", input_type=Boolean(optional=True), prefix="--unordered-ss", doc=InputDocumentation(doc="(For use with --out-format SS).  Suppress the portion of the\nsufficient statistics concerned with the order in which\ncolumns appear.  Useful for analyses for which order is\nunimportant.")), ToolInput(tag="in_refseq", input_type=File(optional=True), prefix="--refseq", doc=InputDocumentation(doc="Read the complete text of the reference sequence from\n<fname> (FASTA format) and combine it with the contents of\nthe MAF file to produce a complete, ordered representation of\nthe alignment (unaligned regions will be represented by gaps).\nBest used with --out-format SS.  The reference sequence of the\nMAF file is assumed to be the one that appears first in each\nblock.")), ToolInput(tag="in_keep_overlapping", input_type=Boolean(optional=True), prefix="--keep-overlapping", doc=InputDocumentation(doc="Keep blocks in MAF that have overlapping coordinates in the\nreference (1st) sequence (by default, only the first one is\nkept).  Useful in extracting unordered stats from a jumbled\ncollection of MAF blocks (e.g., output of Jim Kent's mafFrags\nprogram).  Cannot be used with --refseq, --features, or\n--cats-cycle.")), ToolInput(tag="in_features", input_type=File(optional=True), prefix="--features", doc=InputDocumentation(doc="(Requires --catmap) Read sequence annotations from the\nspecified file (GFF) and label the columns of the alignment\naccordingly.  Note: UCSC BED and genepred formats are now\nrecognized as well.")), ToolInput(tag="in_cat_map", input_type=File(optional=True), prefix="--catmap", doc=InputDocumentation(doc="|<string>\n(optionally use with --features) Mapping of feature types to\ncategory numbers.  Can either give a filename or an 'inline'\ndescription of a simple category map, e.g., --catmap 'NCATS =\n3 ; CDS 1-3' or --catmap 'NCATS = 1 ; UTR 1'.")), ToolInput(tag="in_cats_cycle", input_type=Int(optional=True), prefix="--cats-cycle", doc=InputDocumentation(doc="(alternative to --features and --catmap) Assign site categories in\ncycles of the specified size, e.g., as 1,2,3,...,1,2,3 (for\ncycle_size == 3).  Useful for in-frame coding sequence, or to\npartition a data set into nonoverlapping tuples of columns\n(use with --do-cats).")), ToolInput(tag="in_do_cats", input_type=Int(optional=True), prefix="--do-cats", doc=InputDocumentation(doc="(For use with --features or --cats-cycle)  Obtain\nsufficient statistics only for the specified categories\n(comma-delimited list, by number).")), ToolInput(tag="in_codons", input_type=Boolean(optional=True), prefix="--codons", doc=InputDocumentation(doc="Extract sufficient statistics for in-frame codons.  Implies\n--tuple-size 3 --cats-cycle 3 --do-cats 3.  Not appropriate\nfor use with --features/--catmap.")), ToolInput(tag="in_reverse_groups", input_type=String(optional=True), prefix="--reverse-groups", doc=InputDocumentation(doc="(For use with --features) Group features by <tag> (e.g.,\n'transcript_id' or 'exon_id') and reverse complement\nsegments of the alignment corresponding to groups on the\nreverse strand.  Groups must be non-overlapping (see refeature\n--unique).  Useful when extracting sufficient statistics for\nstrand-specific site categories (e.g., codon positions).")), ToolInput(tag="in_four_d", input_type=Boolean(optional=True), prefix="--4d", doc=InputDocumentation(doc="(For use with --features; assumes coding regions have feature\ntype 'CDS')  Extract sufficient statistics for fourfold\ndegenerate synonymous sites.  Implies --out-format SS\n--unordered-stats --tuple-size 3 --reverse-groups transcript_id.")), ToolInput(tag="in_clean_coding", input_type=Int(optional=True), prefix="--clean-coding", doc=InputDocumentation(doc="Clean an alignment of coding sequences with respect to a named\nreference sequence.  Removes sites with gaps and blocks of\ngapless sites smaller than 10 codons in length, ensures\neverything is in-frame wrt reference sequence, prohibits\nin-frame stop codons.  Reference sequence must begin with a\nstart codon and end with a stop codon.")), ToolInput(tag="in_clean_indels", input_type=Int(optional=True), prefix="--clean-indels", doc=InputDocumentation(doc="Clean an alignment with special attention to indels.  Sites\nwith fewer than <nseqs> bases are removed; bases adjacent to\nindels, and short gapless subsequences, are replaced with Ns.\nIf used with --tuple-size, then <tup_size>-1 columns of Ns\nwill be retained between columns not adjacent in the original\nalignment.  Frame is not considered.")), ToolInput(tag="in_alignments_dot", input_type=String(), position=0, doc=InputDocumentation(doc="Can extract a sub-alignment from an alignment (by row"))], outputs=[ToolOutput(tag="out_out_format", output_type=File(optional=True), selector=InputSelector(input_to_select="in_out_format", type_hint=File()), doc=OutputDocumentation(doc="|FASTA|MPM|SS\n(Default FASTA)  Output file format.")), ToolOutput(tag="out_aggregate", output_type=File(optional=True), selector=InputSelector(input_to_select="in_aggregate", type_hint=File()), doc=OutputDocumentation(doc="(Not compatible with --start or --end) Create an aggregate\nalignment from a set of alignment files, by concatenating\nindividual alignments.  If used with --out-format SS and\n--unordered-ss, the aggregate alignment will never be created\nexplicitly (recommended for large data sets).  The argument\n<name_list> must be a list of sequence names, including all\nnames in all specified alignments (missing sequences will be\nreplaced by rows of missing data).  The standard <msa_fname>\nargument should be replaced with a list of (whitespace-\nseparated) file names.")), ToolOutput(tag="out_split_all", output_type=File(optional=True), selector=InputSelector(input_to_select="in_split_all", type_hint=File()), doc=OutputDocumentation(doc="Split output alignment into separate fasta files by species.\nFile naming convention is filename_root.species.fa. If used with\n--gap-strip, gap characters will be stripped from all output files.\nIn this case, '--gap-strip <s>' should NOT be used (ALL or ANY\nshould both work fine).")), ToolOutput(tag="out_refseq", output_type=File(optional=True), selector=InputSelector(input_to_select="in_refseq", type_hint=File()), doc=OutputDocumentation(doc="Read the complete text of the reference sequence from\n<fname> (FASTA format) and combine it with the contents of\nthe MAF file to produce a complete, ordered representation of\nthe alignment (unaligned regions will be represented by gaps).\nBest used with --out-format SS.  The reference sequence of the\nMAF file is assumed to be the one that appears first in each\nblock."))], container=None, version="v0.1.0")


