Finestructure_V0_1_0 = CommandToolBuilder(tool="finestructure", base_command=["finestructure"], inputs=[ToolInput(tag="in_method_use_omcmcmethod", input_type=String(optional=True), prefix="-m", doc=InputDocumentation(doc="Method to use.  Default: oMCMC.\n<method> is either MCMCwithTree, oMCMC (MCMC without tree),\nSplitMerge, Tree, or admixture, or a contraction of any.\nNote that admixture does not infer the population\nso should be provided with a good one in 'initialpopfile'.")), ToolInput(tag="in_initial_number_populations", input_type=Int(optional=True), prefix="-I", doc=InputDocumentation(doc="Initial number of populations.  <x> is either a number\nor 'n' for the number of individuals, or 'l' for label detected\npopulations.  Default is 1.")), ToolInput(tag="in_sets_rng_seed", input_type=Int(optional=True), prefix="-s", doc=InputDocumentation(doc="Sets the RNG seed to s (>0)")), ToolInput(tag="in_ignores_first_lines", input_type=File(optional=True), prefix="-i", doc=InputDocumentation(doc="Ignores the first i lines of the input file")), ToolInput(tag="in_number_burn_iterations", input_type=Int(optional=True), prefix="-x", doc=InputDocumentation(doc="Number of burn in iterations for MCMC method.")), ToolInput(tag="in_number_sample_iterations", input_type=Int(optional=True), prefix="-y", doc=InputDocumentation(doc="Number of sample iterations for MCMC method.")), ToolInput(tag="in_thin_interval_output", input_type=File(optional=True), prefix="-z", doc=InputDocumentation(doc="Thin interval in the output file, for MCMC method.")), ToolInput(tag="in_maximum_number_tree", input_type=Int(optional=True), prefix="-t", doc=InputDocumentation(doc="Maximum number of tree comparisons for splitting/merging.")), ToolInput(tag="in_fix_number_populations", input_type=Boolean(optional=True), prefix="-K", doc=InputDocumentation(doc="Fix the number of populations to whatever you started with.\nThis would be set by '-I' or by an initial state file.")), ToolInput(tag="in_specify_length_datafile", input_type=File(optional=True), prefix="-l", doc=InputDocumentation(doc="Specify the average copy length datafile.  -i,-X,-Y options\n*preciding* this file will affect this read; you can set different\noptions for the copy rate datafile by specifying these -i,-X,-Y again\nafter the -l option.")), ToolInput(tag="in_use_inference_method", input_type=Int(optional=True), prefix="-u", doc=InputDocumentation(doc="Use a data inference method; one of :\ncounts: use only the copy counts data. (default if -l not specified)\nlengths: use only the copy length data (still needs valid counts data!)\ntotallengths: use the mean length of chunk sizes\nall: use all data (careful: this may not be statistically valid).\ndefault: use counts and totallengths (default with -l specified).")), ToolInput(tag="in_set_alpha_prior", input_type=Int(optional=True), prefix="-a", doc=InputDocumentation(doc="Set alpha, the prior of the number of parameters\n(default: 1.0).")), ToolInput(tag="in_set_likelihood_factor", input_type=Int(optional=True), prefix="-c", doc=InputDocumentation(doc="Set the likelihood correction factor: L_{used}=L^{1/<corfactor>}.\n(default: 1.0)")), ToolInput(tag="in_choose_model_betaee", input_type=Int(optional=True), prefix="-B", doc=InputDocumentation(doc="Choose a model for beta:\n1/e/E:  Equipartition model of Pella and Masuda.\n2/c/C:  Constant model.\n4/o/O:  F model of Falush et al 2003 with a single parameter\nfor all populations (default).")), ToolInput(tag="in_num_hyperparameters_models", input_type=Int(optional=True), prefix="-b", doc=InputDocumentation(doc="(,<num>,..) Hyperparameters for ALL models, in the order COUNTS,LENGTHS,MEANS.\nCOUNTS: *must* be included, even if count matrix not used!\nFor model 1, there are no parameters.\nFor model 2, set the prior of the distribution of\npopulation sizes (each population has beta_i=<num>).\n(default: 1.0).\nFor model 4, set the hyperprior of the distribution of\ndelta and F. Parameters are\n(k_f,k_delta,theta_f,theta_delta) for the parameters of the\ngamma distribution F~Gamma(k_f,theta_f),\nand delta~Gamma(k_delta,theta_delta)\n(default: -b 2,2,0.01,0.01).\nLENGTHS: 8 parameters:\n(k_alpha0,k_beta0,k_alpha,k_beta,beta_alpha0,beta_beta0,beta_alpha,beta_beta)\nMEANS: 6 parameters:\n(k_betamu, k_alphamu, k_kappa, beta_alphamu,beta_betamu,beta_kappa)\nSet K parameters negative for fixed =|k|\ne.g. when finding a tree given the mean parameters.")), ToolInput(tag="in_specify_type_be", input_type=Int(optional=True), prefix="-M", doc=InputDocumentation(doc="Specify the type of inference model for chunk counts.\n<modeltype> accept contractions and lower case, and can be:\n1 or Finestructure: standard finestructure model (default).\n2 or Normalised: Normalise data row and columns within a population.\n3 or MergeOnly: As 2, but only compare populations being merged or split.\n4 or Individual: Prior is placed on individual rows instead of\npopulation rows. (slowest model).")), ToolInput(tag="in_extract_details_state", input_type=File(optional=True), prefix="-e", doc=InputDocumentation(doc="Extract details from a state; can be (a unique contraction of):\nbeta: the parameter matrix\nX: the copying data matrix for populations\nX2: the normalised copying matrix\nmaxstate: maximum observed posterior probability state\nmeancoincidence: the mean coincidence matrix\nmerge<:value><:split>: create a merge(or split)\npopulation from the mean coincidence.\nadmixture: gets the population as an admixture matrix.\nPmatrix: gets the P matrix for the admixture.\nrange:<from>:<to> gets the iterations in the specified range.\nthin:<step>: thins the output by step.\nprobability: get the posterior probability of the data\ngiven the conditions of the outputfile.\nlikelihood: samples the likelihood of the data given the conditions\nin the outputfile.\ntree: extract the tree in newick format and print it to a FOURTH file")), ToolInput(tag="in_fix_populations_specified", input_type=File(optional=True), prefix="-F", doc=InputDocumentation(doc="Fix the populations specified in the file.  They should be specified as\npopulation format, i.e. PopA(ind1,ind2) would fix the data rows ind1 and ind2\nto always be in the same population (they form a 'super individual')\ncalled PopA. Continents are specified with a * before the name, and are treated\nspecially in the tree building phase,  i.e. *ContA(ind1,ind2).  Continents\nare not merged with the rest of the tree.")), ToolInput(tag="in_when_using_initialisation", input_type=Int(optional=True), prefix="-T", doc=InputDocumentation(doc="When using a merge tree, initialisation can be set to the following:\n1:      Use the initial state 'as is'.\n2:      Perform merging to get to best posterior state.\n3:      Perform full range of moves to to get to best posterior state.\nThis is the default.  Set number of attempts with -x <num>.\n4:      As 1, but don't flatten maximum copy rates for the main tree.\n5:      As 2, but don't flatten maximum copy rates for the main tree.\n6:      As 3, but don't flatten maximum copy rates for the main tree.\n7:      As 1, but maximise hyperparameters between merges.\n8:      As 2, but maximise hyperparameters between merges.\n9:      As 3, but maximise hyperparameters between merges.")), ToolInput(tag="in_file_containing_state", input_type=File(optional=True), prefix="-o", doc=InputDocumentation(doc="File containing a state to use for ordering, if not the main file.")), ToolInput(tag="in_change_building_algorithm", input_type=Int(optional=True), prefix="-k", doc=InputDocumentation(doc="Change the tree building algorithm.\n0:      Discard all ordering and likelihood information (default).\n1:      Maintain ordering.\n2:      Maintain ordering and likelihood.")), ToolInput(tag="in_specifies_there_are_row_names", input_type=Boolean(optional=True), prefix="-X", doc=InputDocumentation(doc="Specifies that there are row names in the data (not necessary for\nChromoPainter or ChromoCombine style files.)")), ToolInput(tag="in_specifies_there_are_column_names", input_type=Boolean(optional=True), prefix="-Y", doc=InputDocumentation(doc="Specifies that there are column names in the data file (as -X, not necessary.)")), ToolInput(tag="in_verbose_mode", input_type=Boolean(optional=True), prefix="-v", doc=InputDocumentation(doc="Verbose mode")), ToolInput(tag="in_print_version_info", input_type=Boolean(optional=True), prefix="-V", doc=InputDocumentation(doc="Print Version info"))], outputs=[ToolOutput(tag="out_thin_interval_output", output_type=File(optional=True), selector=InputSelector(input_to_select="in_thin_interval_output", type_hint=File()), doc=OutputDocumentation(doc="Thin interval in the output file, for MCMC method.")), ToolOutput(tag="out_extract_details_state", output_type=File(optional=True), selector=InputSelector(input_to_select="in_extract_details_state", type_hint=File()), doc=OutputDocumentation(doc="Extract details from a state; can be (a unique contraction of):\nbeta: the parameter matrix\nX: the copying data matrix for populations\nX2: the normalised copying matrix\nmaxstate: maximum observed posterior probability state\nmeancoincidence: the mean coincidence matrix\nmerge<:value><:split>: create a merge(or split)\npopulation from the mean coincidence.\nadmixture: gets the population as an admixture matrix.\nPmatrix: gets the P matrix for the admixture.\nrange:<from>:<to> gets the iterations in the specified range.\nthin:<step>: thins the output by step.\nprobability: get the posterior probability of the data\ngiven the conditions of the outputfile.\nlikelihood: samples the likelihood of the data given the conditions\nin the outputfile.\ntree: extract the tree in newick format and print it to a FOURTH file"))], container=None, version="v0.1.0")


