from datetime import datetime
from typing import List, Optional, Dict, Any

from janis_core import *
from janis_core.types.common_data_types import Boolean, File, Int, Array, Directory, String, Float

Snakelines_V0_1_0 = CommandToolBuilder(tool="snakelines", base_command=["snakelines"], inputs=[ToolInput(tag="in_dry_run", input_type=Boolean(optional=True), prefix="--dry-run", doc=InputDocumentation(doc="Do not execute anything, and display what would be\ndone. If you have a very large workflow, use --dry-run\n--quiet to just print a summary of the DAG of jobs.")), ToolInput(tag="in_profile", input_type=File(optional=True), prefix="--profile", doc=InputDocumentation(doc="Name of profile to use for configuring Snakemake.\nSnakemake will search for a corresponding folder in\n/etc/xdg/snakemake and /root/.config/snakemake.\nAlternatively, this can be an absolute or relative\npath. The profile folder has to contain a file\n'config.yaml'. This file can be used to set default\nvalues for command line options in YAML format. For\nexample, '--cluster qsub' becomes 'cluster: qsub' in\nthe YAML file. Profiles can be obtained from\nhttps://github.com/snakemake-profiles.")), ToolInput(tag="in_cache", input_type=Boolean(optional=True), prefix="--cache", doc=InputDocumentation(doc="[RULE [RULE ...]]\nStore output files of given rules in a central cache\ngiven by the environment variable\n$SNAKEMAKE_OUTPUT_CACHE. Likewise, retrieve output\nfiles of the given rules from this cache if they have\nbeen created before (by anybody writing to the same\ncache), instead of actually executing the rules.\nOutput files are identified by hashing all steps,\nparameters and software stack (conda envs or\ncontainers) needed to create them.")), ToolInput(tag="in_snake_file", input_type=File(optional=True), prefix="--snakefile", doc=InputDocumentation(doc="The workflow definition in form of a\nsnakefile.Usually, you should not need to specify\nthis. By default, Snakemake will search for\n'Snakefile', 'snakefile', 'workflow/Snakefile',\n'workflow/snakefile' beneath the current working\ndirectory, in this order. Only if you definitely want\na different layout, you need to use this parameter.")), ToolInput(tag="in_cores", input_type=Boolean(optional=True), prefix="--cores", doc=InputDocumentation(doc="[N], --jobs [N], -j [N]\nUse at most N CPU cores/jobs in parallel. If N is\nomitted or 'all', the limit is set to the number of\navailable CPU cores.")), ToolInput(tag="in_local_cores", input_type=Int(optional=True), prefix="--local-cores", doc=InputDocumentation(doc="In cluster mode, use at most N cores of the host\nmachine in parallel (default: number of CPU cores of\nthe host). The cores are used to execute local rules.\nThis option is ignored when not in cluster mode.")), ToolInput(tag="in_resources", input_type=Boolean(optional=True), prefix="--resources", doc=InputDocumentation(doc="[NAME=INT [NAME=INT ...]], --res [NAME=INT [NAME=INT ...]]\nDefine additional resources that shall constrain the\nscheduling analogously to threads (see above). A\nresource is defined as a name and an integer value.\nE.g. --resources gpu=1. Rules can use resources by\ndefining the resource keyword, e.g. resources: gpu=1.\nIf now two rules require 1 of the resource 'gpu' they\nwon't be run in parallel by the scheduler.")), ToolInput(tag="in_set_threads", input_type=Boolean(optional=True), prefix="--set-threads", doc=InputDocumentation(doc="[RULE=THREADS [RULE=THREADS ...]]\nOverwrite thread usage of rules. This allows to fine-\ntune workflow parallelization. In particular, this is\nhelpful to target certain cluster nodes by e.g.\nshifting a rule to use more, or less threads than\ndefined in the workflow. Thereby, THREADS has to be a\npositive integer, and RULE has to be the name of the\nrule.")), ToolInput(tag="in_default_resources", input_type=Boolean(optional=True), prefix="--default-resources", doc=InputDocumentation(doc="[NAME=INT [NAME=INT ...]], --default-res [NAME=INT [NAME=INT ...]]\nDefine default values of resources for rules that do\nnot define their own values. In addition to plain\nintegers, python expressions over inputsize are\nallowed (e.g. '2*input.size_mb').When specifying this\nwithout any arguments (--default-resources), it\ndefines 'mem_mb=max(2*input.size_mb, 1000)'\n'disk_mb=max(2*input.size_mb, 1000)', i.e., default\ndisk and mem usage is twice the input file size but at\nleast 1GB.")), ToolInput(tag="in_config", input_type=Boolean(optional=True), prefix="--config", doc=InputDocumentation(doc="[KEY=VALUE [KEY=VALUE ...]], -C [KEY=VALUE [KEY=VALUE ...]]\nSet or overwrite values in the workflow config object.\nThe workflow config object is accessible as variable\nconfig inside the workflow. Default values can be set\nby providing a JSON file (see Documentation).")), ToolInput(tag="in_config_files", input_type=Array(t=File(), optional=True), prefix="--configfiles", doc=InputDocumentation(doc="Specify or overwrite the config file of the workflow\n(see the docs). Values specified in JSON or YAML\nformat are available in the global config dictionary\ninside the workflow. Multiple files overwrite each\nother in the given order.")), ToolInput(tag="in_directory", input_type=Directory(optional=True), prefix="--directory", doc=InputDocumentation(doc="Specify working directory (relative paths in the\nsnakefile will use this as their origin).")), ToolInput(tag="in_touch", input_type=Boolean(optional=True), prefix="--touch", doc=InputDocumentation(doc="Touch output files (mark them up to date without\nreally changing them) instead of running their\ncommands. This is used to pretend that the rules were\nexecuted, in order to fool future invocations of\nsnakemake. Fails if a file does not yet exist. Note\nthat this will only touch files that would otherwise\nbe recreated by Snakemake (e.g. because their input\nfiles are newer). For enforcing a touch, combine this\nwith --force, --forceall, or --forcerun. Note however\nthat you loose the provenance information when the\nfiles have been created in realitiy. Hence, this\nshould be used only as a last resort.")), ToolInput(tag="in_keep_going", input_type=Boolean(optional=True), prefix="--keep-going", doc=InputDocumentation(doc="Go on with independent jobs if a job fails.")), ToolInput(tag="in_force", input_type=Boolean(optional=True), prefix="--force", doc=InputDocumentation(doc="Force the execution of the selected target or the\nfirst rule regardless of already created output.")), ToolInput(tag="in_force_all", input_type=Boolean(optional=True), prefix="--forceall", doc=InputDocumentation(doc="Force the execution of the selected (or the first)\nrule and all rules it is dependent on regardless of\nalready created output.")), ToolInput(tag="in_force_run", input_type=Boolean(optional=True), prefix="--forcerun", doc=InputDocumentation(doc="[TARGET [TARGET ...]], -R [TARGET [TARGET ...]]\nForce the re-execution or creation of the given rules\nor files. Use this option if you changed a rule and\nwant to have all its output in your workflow updated.")), ToolInput(tag="in_prioritize", input_type=Array(t=String(), optional=True), prefix="--prioritize", doc=InputDocumentation(doc="Tell the scheduler to assign creation of given targets\n(and all their dependencies) highest priority.\n(EXPERIMENTAL)")), ToolInput(tag="in_batch", input_type=Int(optional=True), prefix="--batch", doc=InputDocumentation(doc="=BATCH/BATCHES\nOnly create the given BATCH of the input files of the\ngiven RULE. This can be used to iteratively run parts\nof very large workflows. Only the execution plan of\nthe relevant part of the workflow has to be\ncalculated, thereby speeding up DAG computation. It is\nrecommended to provide the most suitable rule for\nbatching when documenting a workflow. It should be\nsome aggregating rule that would be executed only\nonce, and has a large number of input files. For\nexample, it can be a rule that aggregates over\nsamples.")), ToolInput(tag="in_until", input_type=Array(t=String(), optional=True), prefix="--until", doc=InputDocumentation(doc="Runs the pipeline until it reaches the specified rules\nor files. Only runs jobs that are dependencies of the\nspecified rule or files, does not run sibling DAGs.")), ToolInput(tag="in_omit_from", input_type=Array(t=String(), optional=True), prefix="--omit-from", doc=InputDocumentation(doc="Prevent the execution or creation of the given rules\nor files as well as any rules or files that are\ndownstream of these targets in the DAG. Also runs jobs\nin sibling DAGs that are independent of the rules or\nfiles specified here.")), ToolInput(tag="in_rerun_incomplete", input_type=Boolean(optional=True), prefix="--rerun-incomplete", doc=InputDocumentation(doc="Re-run all jobs the output of which is recognized as\nincomplete.")), ToolInput(tag="in_shadow_prefix", input_type=Directory(optional=True), prefix="--shadow-prefix", doc=InputDocumentation(doc="Specify a directory in which the 'shadow' directory is\ncreated. If not supplied, the value is set to the\n'.snakemake' directory relative to the working\ndirectory.")), ToolInput(tag="in_report", input_type=Boolean(optional=True), prefix="--report", doc=InputDocumentation(doc="[HTMLFILE]   Create an HTML report with results and statistics. If\nno filename is given, report.html is the default.")), ToolInput(tag="in_lint", input_type=Boolean(optional=True), prefix="--lint", doc=InputDocumentation(doc="[{text,json}]  Perform linting on the given workflow. This will print\nsnakemake specific suggestions to improve code quality\n(work in progress, more lints to be added in the\nfuture). If no argument is provided, plain text output\nis used.")), ToolInput(tag="in_export_cwl", input_type=File(optional=True), prefix="--export-cwl", doc=InputDocumentation(doc="Compile workflow to CWL and store it in given FILE.")), ToolInput(tag="in_list", input_type=Boolean(optional=True), prefix="--list", doc=InputDocumentation(doc="Show available rules in given Snakefile.")), ToolInput(tag="in_list_target_rules", input_type=Boolean(optional=True), prefix="--list-target-rules", doc=InputDocumentation(doc="Show available target rules in given Snakefile.")), ToolInput(tag="in_dag", input_type=Boolean(optional=True), prefix="--dag", doc=InputDocumentation(doc="Do not execute anything and print the directed acyclic\ngraph of jobs in the dot language. Recommended use on\nUnix systems: snakemake --dag | dot | display")), ToolInput(tag="in_rule_graph", input_type=Boolean(optional=True), prefix="--rulegraph", doc=InputDocumentation(doc="Do not execute anything and print the dependency graph\nof rules in the dot language. This will be less\ncrowded than above DAG of jobs, but also show less\ninformation. Note that each rule is displayed once,\nhence the displayed graph will be cyclic if a rule\nappears in several steps of the workflow. Use this if\nabove option leads to a DAG that is too large.\nRecommended use on Unix systems: snakemake --rulegraph\n| dot | display")), ToolInput(tag="in_file_graph", input_type=Boolean(optional=True), prefix="--filegraph", doc=InputDocumentation(doc="Do not execute anything and print the dependency graph\nof rules with their input and output files in the dot\nlanguage. This is an intermediate solution between\nabove DAG of jobs and the rule graph. Note that each\nrule is displayed once, hence the displayed graph will\nbe cyclic if a rule appears in several steps of the\nworkflow. Use this if above option leads to a DAG that\nis too large. Recommended use on Unix systems:\nsnakemake --filegraph | dot | display")), ToolInput(tag="in_d_three_dag", input_type=Boolean(optional=True), prefix="--d3dag", doc=InputDocumentation(doc="Print the DAG in D3.js compatible JSON format.")), ToolInput(tag="in_summary", input_type=Boolean(optional=True), prefix="--summary", doc=InputDocumentation(doc="Print a summary of all files created by the workflow.\nThe has the following columns: filename, modification\ntime, rule version, status, plan. Thereby rule version\ncontains the versionthe file was created with (see the\nversion keyword of rules), and status denotes whether\nthe file is missing, its input files are newer or if\nversion or implementation of the rule changed since\nfile creation. Finally the last column denotes whether\nthe file will be updated or created during the next\nworkflow execution.")), ToolInput(tag="in_detailed_summary", input_type=Boolean(optional=True), prefix="--detailed-summary", doc=InputDocumentation(doc="Print a summary of all files created by the workflow.\nThe has the following columns: filename, modification\ntime, rule version, input file(s), shell command,\nstatus, plan. Thereby rule version contains the\nversion the file was created with (see the version\nkeyword of rules), and status denotes whether the file\nis missing, its input files are newer or if version or\nimplementation of the rule changed since file\ncreation. The input file and shell command columns are\nself explanatory. Finally the last column denotes\nwhether the file will be updated or created during the\nnext workflow execution.")), ToolInput(tag="in_archive", input_type=File(optional=True), prefix="--archive", doc=InputDocumentation(doc="Archive the workflow into the given tar archive FILE.\nThe archive will be created such that the workflow can\nbe re-executed on a vanilla system. The function needs\nconda and git to be installed. It will archive every\nfile that is under git version control. Note that it\nis best practice to have the Snakefile, config files,\nand scripts under version control. Hence, they will be\nincluded in the archive. Further, it will add input\nfiles that are not generated by by the workflow itself\nand conda environments. Note that symlinks are\ndereferenced. Supported formats are .tar, .tar.gz,\n.tar.bz2 and .tar.xz.")), ToolInput(tag="in_cleanup_metadata", input_type=Array(t=File(), optional=True), prefix="--cleanup-metadata", doc=InputDocumentation(doc="Cleanup the metadata of given files. That means that\nsnakemake removes any tracked version info, and any\nmarks that files are incomplete.")), ToolInput(tag="in_cleanup_shadow", input_type=Boolean(optional=True), prefix="--cleanup-shadow", doc=InputDocumentation(doc="Cleanup old shadow directories which have not been\ndeleted due to failures or power loss.")), ToolInput(tag="in_skip_script_cleanup", input_type=Boolean(optional=True), prefix="--skip-script-cleanup", doc=InputDocumentation(doc="Don't delete wrapper scripts used for execution")), ToolInput(tag="in_unlock", input_type=Boolean(optional=True), prefix="--unlock", doc=InputDocumentation(doc="Remove a lock on the working directory.")), ToolInput(tag="in_list_version_changes", input_type=Boolean(optional=True), prefix="--list-version-changes", doc=InputDocumentation(doc="List all output files that have been created with a\ndifferent version (as determined by the version\nkeyword).")), ToolInput(tag="in_list_code_changes", input_type=Boolean(optional=True), prefix="--list-code-changes", doc=InputDocumentation(doc="List all output files for which the rule body (run or\nshell) have changed in the Snakefile.")), ToolInput(tag="in_list_input_changes", input_type=Boolean(optional=True), prefix="--list-input-changes", doc=InputDocumentation(doc="List all output files for which the defined input\nfiles have changed in the Snakefile (e.g. new input\nfiles were added in the rule definition or files were\nrenamed). For listing input file modification in the\nfilesystem, use --summary.")), ToolInput(tag="in_list_params_changes", input_type=Boolean(optional=True), prefix="--list-params-changes", doc=InputDocumentation(doc="List all output files for which the defined params\nhave changed in the Snakefile.")), ToolInput(tag="in_list_untracked", input_type=Boolean(optional=True), prefix="--list-untracked", doc=InputDocumentation(doc="List all files in the working directory that are not\nused in the workflow. This can be used e.g. for\nidentifying leftover files. Hidden files and\ndirectories are ignored.")), ToolInput(tag="in_delete_all_output", input_type=Boolean(optional=True), prefix="--delete-all-output", doc=InputDocumentation(doc="Remove all files generated by the workflow. Use\ntogether with --dry-run to list files without actually\ndeleting anything. Note that this will not recurse\ninto subworkflows. Write-protected files are not\nremoved. Nevertheless, use with care!")), ToolInput(tag="in_delete_temp_output", input_type=Boolean(optional=True), prefix="--delete-temp-output", doc=InputDocumentation(doc="Remove all temporary files generated by the workflow.\nUse together with --dry-run to list files without\nactually deleting anything. Note that this will not\nrecurse into subworkflows.")), ToolInput(tag="in_bash_completion", input_type=Boolean(optional=True), prefix="--bash-completion", doc=InputDocumentation(doc="Output code to register bash completion for snakemake.\nPut the following in your .bashrc (including the\naccents): `snakemake --bash-completion` or issue it in\nan open terminal session.")), ToolInput(tag="in_keep_incomplete", input_type=Boolean(optional=True), prefix="--keep-incomplete", doc=InputDocumentation(doc="Do not remove incomplete output files by failed jobs.")), ToolInput(tag="in_reason", input_type=Boolean(optional=True), prefix="--reason", doc=InputDocumentation(doc="Print the reason for each executed rule.")), ToolInput(tag="in_gui", input_type=Boolean(optional=True), prefix="--gui", doc=InputDocumentation(doc="[PORT]          Serve an HTML based user interface to the given\nnetwork and port e.g. 168.129.10.15:8000. By default\nSnakemake is only available in the local network\n(default port: 8000). To make Snakemake listen to all\nip addresses add the special host address 0.0.0.0 to\nthe url (0.0.0.0:8000). This is important if Snakemake\nis used in a virtualised environment like Docker. If\npossible, a browser window is opened.")), ToolInput(tag="in_print_shell_cmds", input_type=Boolean(optional=True), prefix="--printshellcmds", doc=InputDocumentation(doc="Print out the shell commands that will be executed.")), ToolInput(tag="in_debug_dag", input_type=Boolean(optional=True), prefix="--debug-dag", doc=InputDocumentation(doc="Print candidate and selected jobs (including their\nwildcards) while inferring DAG. This can help to debug\nunexpected DAG topology or errors.")), ToolInput(tag="in_stats", input_type=File(optional=True), prefix="--stats", doc=InputDocumentation(doc="Write stats about Snakefile execution in JSON format\nto the given file.")), ToolInput(tag="in_no_color", input_type=Boolean(optional=True), prefix="--nocolor", doc=InputDocumentation(doc="Do not use a colored output.")), ToolInput(tag="in_quiet", input_type=Boolean(optional=True), prefix="--quiet", doc=InputDocumentation(doc="Do not output any progress or rule information.")), ToolInput(tag="in_print_compilation", input_type=Boolean(optional=True), prefix="--print-compilation", doc=InputDocumentation(doc="Print the python representation of the workflow.")), ToolInput(tag="in_verbose", input_type=Boolean(optional=True), prefix="--verbose", doc=InputDocumentation(doc="Print debugging output.")), ToolInput(tag="in_force_use_threads", input_type=Boolean(optional=True), prefix="--force-use-threads", doc=InputDocumentation(doc="Force threads rather than processes. Helpful if shared\nmemory (/dev/shm) is full or unavailable.")), ToolInput(tag="in_allow_ambiguity", input_type=Boolean(optional=True), prefix="--allow-ambiguity", doc=InputDocumentation(doc="Don't check for ambiguous rules and simply use the\nfirst if several can produce the same file. This\nallows the user to prioritize rules by their order in\nthe snakefile.")), ToolInput(tag="in_no_lock", input_type=Boolean(optional=True), prefix="--nolock", doc=InputDocumentation(doc="Do not lock the working directory")), ToolInput(tag="in_ignore_incomplete", input_type=Boolean(optional=True), prefix="--ignore-incomplete", doc=InputDocumentation(doc="Do not check for incomplete output files.")), ToolInput(tag="in_latency_wait", input_type=File(optional=True), prefix="--latency-wait", doc=InputDocumentation(doc="Wait given seconds if an output file of a job is not\npresent after the job finished. This helps if your\nfilesystem suffers from latency (default 5).")), ToolInput(tag="in_wait_for_files", input_type=Boolean(optional=True), prefix="--wait-for-files", doc=InputDocumentation(doc="[FILE [FILE ...]]\nWait --latency-wait seconds for these files to be\npresent before executing the workflow. This option is\nused internally to handle filesystem latency in\ncluster environments.")), ToolInput(tag="in_no_temp", input_type=Boolean(optional=True), prefix="--notemp", doc=InputDocumentation(doc="Ignore temp() declarations. This is useful when\nrunning only a part of the workflow, since temp()\nwould lead to deletion of probably needed files by\nother parts of the workflow.")), ToolInput(tag="in_keep_remote", input_type=Boolean(optional=True), prefix="--keep-remote", doc=InputDocumentation(doc="Keep local copies of remote input files.")), ToolInput(tag="in_keep_target_files", input_type=Boolean(optional=True), prefix="--keep-target-files", doc=InputDocumentation(doc="Do not adjust the paths of given target files relative\nto the working directory.")), ToolInput(tag="in_allowed_rules", input_type=Array(t=String(), optional=True), prefix="--allowed-rules", doc=InputDocumentation(doc="Only consider given rules. If omitted, all rules in\nSnakefile are used. Note that this is intended\nprimarily for internal use and may lead to unexpected\nresults otherwise.")), ToolInput(tag="in_max_jobs_per_second", input_type=Int(optional=True), prefix="--max-jobs-per-second", doc=InputDocumentation(doc="Maximal number of cluster/drmaa jobs per second,\ndefault is 10, fractions allowed.")), ToolInput(tag="in_max_status_checks_per_second", input_type=Int(optional=True), prefix="--max-status-checks-per-second", doc=InputDocumentation(doc="Maximal number of job status checks per second,\ndefault is 10, fractions allowed.")), ToolInput(tag="in_restart_times", input_type=Int(optional=True), prefix="--restart-times", doc=InputDocumentation(doc="Number of times to restart failing jobs (defaults to\n0).")), ToolInput(tag="in_attempt", input_type=Int(optional=True), prefix="--attempt", doc=InputDocumentation(doc="Internal use only: define the initial value of the\nattempt parameter (default: 1).")), ToolInput(tag="in_wrapper_prefix", input_type=File(optional=True), prefix="--wrapper-prefix", doc=InputDocumentation(doc="Prefix for URL created from wrapper directive\n(default: https://github.com/snakemake/snakemake-\nwrappers/raw/). Set this to a different URL to use\nyour fork or a local clone of the repository, e.g.,\nuse a git URL like\n'git+file://path/to/your/local/clone@'.")), ToolInput(tag="in_default_remote_provider", input_type=String(optional=True), prefix="--default-remote-provider", doc=InputDocumentation(doc="Specify default remote provider to be used for all\ninput and output files that don't yet specify one.")), ToolInput(tag="in_default_remote_prefix", input_type=String(optional=True), prefix="--default-remote-prefix", doc=InputDocumentation(doc="Specify prefix for default remote provider. E.g. a\nbucket name.")), ToolInput(tag="in_no_shared_fs", input_type=Boolean(optional=True), prefix="--no-shared-fs", doc=InputDocumentation(doc="Do not assume that jobs share a common file system.\nWhen this flag is activated, Snakemake will assume\nthat the filesystem on a cluster node is not shared\nwith other nodes. For example, this will lead to\ndownloading remote files on each cluster node\nseparately. Further, it won't take special measures to\ndeal with filesystem latency issues. This option will\nin most cases only make sense in combination with\n--default-remote-provider. Further, when using\n--cluster you will have to also provide --cluster-\nstatus. Only activate this if you know what you are\ndoing.")), ToolInput(tag="in_greediness", input_type=Float(optional=True), prefix="--greediness", doc=InputDocumentation(doc="Set the greediness of scheduling. This value between 0\nand 1 determines how careful jobs are selected for\nexecution. The default value (1.0) provides the best\nspeed and still acceptable scheduling quality.")), ToolInput(tag="in_no_hooks", input_type=Boolean(optional=True), prefix="--no-hooks", doc=InputDocumentation(doc="Do not invoke onstart, onsuccess or onerror hooks\nafter execution.")), ToolInput(tag="in_overwrite_shell_cmd", input_type=String(optional=True), prefix="--overwrite-shellcmd", doc=InputDocumentation(doc="Provide a shell command that shall be executed instead\nof those given in the workflow. This is for debugging\npurposes only.")), ToolInput(tag="in_debug", input_type=Boolean(optional=True), prefix="--debug", doc=InputDocumentation(doc="Allow to debug rules with e.g. PDB. This flag allows\nto set breakpoints in run blocks.")), ToolInput(tag="in_runtime_profile", input_type=File(optional=True), prefix="--runtime-profile", doc=InputDocumentation(doc="Profile Snakemake and write the output to FILE. This\nrequires yappi to be installed.")), ToolInput(tag="in_mode", input_type=String(optional=True), prefix="--mode", doc=InputDocumentation(doc="Set execution mode of Snakemake (internal use only).")), ToolInput(tag="in_show_failed_logs", input_type=Boolean(optional=True), prefix="--show-failed-logs", doc=InputDocumentation(doc="Automatically display logs of failed jobs.")), ToolInput(tag="in_log_handler_script", input_type=File(optional=True), prefix="--log-handler-script", doc=InputDocumentation(doc="Provide a custom script containing a function 'def\nlog_handler(msg):'. Snakemake will call this function\nfor every logging output (given as a dictionary\nmsg)allowing to e.g. send notifications in the form of\ne.g. slack messages or emails.")), ToolInput(tag="in_log_service", input_type=String(optional=True), prefix="--log-service", doc=InputDocumentation(doc="Set a specific messaging service for logging\noutput.Snakemake will notify the service on errors and\ncompleted execution.Currently only slack is supported.")), ToolInput(tag="in_cluster", input_type=String(optional=True), prefix="--cluster", doc=InputDocumentation(doc="Execute snakemake rules with the given submit command,\ne.g. qsub. Snakemake compiles jobs into scripts that\nare submitted to the cluster with the given command,\nonce all input files for a particular job are present.\nThe submit command can be decorated to make it aware\nof certain job properties (name, rulename, input,\noutput, params, wildcards, log, threads and\ndependencies (see the argument below)), e.g.: $\nsnakemake --cluster 'qsub -pe threaded {threads}'.")), ToolInput(tag="in_cluster_sync", input_type=String(optional=True), prefix="--cluster-sync", doc=InputDocumentation(doc="cluster submission command will block, returning the\nremote exitstatus upon remote termination (for\nexample, this should be usedif the cluster command is\n'qsub -sync y' (SGE)")), ToolInput(tag="in_drmaa", input_type=Boolean(optional=True), prefix="--drmaa", doc=InputDocumentation(doc="[ARGS]        Execute snakemake on a cluster accessed via DRMAA,\nSnakemake compiles jobs into scripts that are\nsubmitted to the cluster with the given command, once\nall input files for a particular job are present. ARGS\ncan be used to specify options of the underlying\ncluster system, thereby using the job properties name,\nrulename, input, output, params, wildcards, log,\nthreads and dependencies, e.g.: --drmaa ' -pe threaded\n{threads}'. Note that ARGS must be given in quotes and\nwith a leading whitespace.")), ToolInput(tag="in_cluster_config", input_type=File(optional=True), prefix="--cluster-config", doc=InputDocumentation(doc="A JSON or YAML file that defines the wildcards used in\n'cluster'for specific rules, instead of having them\nspecified in the Snakefile. For example, for rule\n'job' you may define: { 'job' : { 'time' : '24:00:00'\n} } to specify the time for rule 'job'. You can\nspecify more than one file. The configuration files\nare merged with later values overriding earlier ones.\nThis option is deprecated in favor of using --profile,\nsee docs.")), ToolInput(tag="in_immediate_submit", input_type=Boolean(optional=True), prefix="--immediate-submit", doc=InputDocumentation(doc="Immediately submit all jobs to the cluster instead of\nwaiting for present input files. This will fail,\nunless you make the cluster aware of job dependencies,\ne.g. via: $ snakemake --cluster 'sbatch --dependency\n{dependencies}. Assuming that your submit script (here\nsbatch) outputs the generated job id to the first\nstdout line, {dependencies} will be filled with space\nseparated job ids this job depends on.")), ToolInput(tag="in_job_script", input_type=Directory(optional=True), prefix="--jobscript", doc=InputDocumentation(doc="Provide a custom job script for submission to the\ncluster. The default script resides as 'jobscript.sh'\nin the installation directory.")), ToolInput(tag="in_job_name", input_type=String(optional=True), prefix="--jobname", doc=InputDocumentation(doc="Provide a custom name for the jobscript that is\nsubmitted to the cluster (see --cluster). NAME is\n'snakejob.{name}.{jobid}.sh' per default. The wildcard\n{jobid} has to be present in the name.")), ToolInput(tag="in_cluster_status", input_type=String(optional=True), prefix="--cluster-status", doc=InputDocumentation(doc="Status command for cluster execution. This is only\nconsidered in combination with the --cluster flag. If\nprovided, Snakemake will use the status command to\ndetermine if a job has finished successfully or\nfailed. For this it is necessary that the submit\ncommand provided to --cluster returns the cluster job\nid. Then, the status command will be invoked with the\njob id. Snakemake expects it to return 'success' if\nthe job was successfull, 'failed' if the job failed\nand 'running' if the job still runs.")), ToolInput(tag="in_drmaa_log_dir", input_type=File(optional=True), prefix="--drmaa-log-dir", doc=InputDocumentation(doc="Specify a directory in which stdout and stderr files\nof DRMAA jobs will be written. The value may be given\nas a relative path, in which case Snakemake will use\nthe current invocation directory as the origin. If\ngiven, this will override any given '-o' and/or '-e'\nnative specification. If not given, all DRMAA stdout\nand stderr files are written to the current working\ndirectory.")), ToolInput(tag="in_ku_bernet_es", input_type=Boolean(optional=True), prefix="--kubernetes", doc=InputDocumentation(doc="[NAMESPACE]\nExecute workflow in a kubernetes cluster (in the\ncloud). NAMESPACE is the namespace you want to use for\nyour job (if nothing specified: 'default'). Usually,\nthis requires --default-remote-provider and --default-\nremote-prefix to be set to a S3 or GS bucket where\nyour . data shall be stored. It is further advisable\nto activate conda integration via --use-conda.")), ToolInput(tag="in_container_image", input_type=String(optional=True), prefix="--container-image", doc=InputDocumentation(doc="Docker image to use, e.g., when submitting jobs to\nkubernetes. By default, this is\n'https://hub.docker.com/r/snakemake/snakemake', tagged\nwith the same version as the currently running\nSnakemake instance. Note that overwriting this value\nis up to your responsibility. Any used image has to\ncontain a working snakemake installation that is\ncompatible with (or ideally the same as) the currently\nrunning version.")), ToolInput(tag="in_tib_anna", input_type=Boolean(optional=True), prefix="--tibanna", doc=InputDocumentation(doc="Execute workflow on AWS cloud using Tibanna. This\nrequires --default-remote-prefix to be set to S3\nbucket name and prefix (e.g.\n'bucketname/subdirectory') where input is already\nstored and output will be sent to. Using --tibanna\nimplies --default-resources is set as default.\nOptionally, use --precommand to specify any\npreparation command to run before snakemake command on\nthe cloud (inside snakemake container on Tibanna VM).\nAlso, --use-conda, --use-singularity, --config,\n--configfile are supported and will be carried over.")), ToolInput(tag="in_tib_anna_sfn", input_type=String(optional=True), prefix="--tibanna-sfn", doc=InputDocumentation(doc="Name of Tibanna Unicorn step function (e.g.\ntibanna_unicorn_monty).This works as serverless\nscheduler/resource allocator and must be deployed\nfirst using tibanna cli. (e.g. tibanna deploy_unicorn\n--usergroup=monty --buckets=bucketname)")), ToolInput(tag="in_pre_command", input_type=File(optional=True), prefix="--precommand", doc=InputDocumentation(doc="Any command to execute before snakemake command on AWS\ncloud such as wget, git clone, unzip, etc. This is\nused with --tibanna.Do not include input/output\ndownload/upload commands - file transfer between S3\nbucket and the run environment (container) is\nautomatically handled by Tibanna.")), ToolInput(tag="in_tib_anna_config", input_type=Array(t=String(), optional=True), prefix="--tibanna-config", doc=InputDocumentation(doc="Additional tibanan config e.g. --tibanna-config\nspot_instance=true subnet=<subnet_id> security\ngroup=<security_group_id>")), ToolInput(tag="in_use_cond_a", input_type=Boolean(optional=True), prefix="--use-conda", doc=InputDocumentation(doc="If defined in the rule, run job in a conda\nenvironment. If this flag is not set, the conda\ndirective is ignored.")), ToolInput(tag="in_list_cond_a_envs", input_type=Boolean(optional=True), prefix="--list-conda-envs", doc=InputDocumentation(doc="List all conda environments and their location on")), ToolInput(tag="in_cond_a_prefix", input_type=File(optional=True), prefix="--conda-prefix", doc=InputDocumentation(doc="Specify a directory in which the 'conda' and 'conda-\narchive' directories are created. These are used to\nstore conda environments and their archives,\nrespectively. If not supplied, the value is set to the\n'.snakemake' directory relative to the invocation\ndirectory. If supplied, the `--use-conda` flag must\nalso be set. The value may be given as a relative\npath, which will be extrapolated to the invocation\ndirectory, or as an absolute path.")), ToolInput(tag="in_create_envs_only", input_type=Boolean(optional=True), prefix="--create-envs-only", doc=InputDocumentation(doc="If specified, only creates the job-specific conda\nenvironments then exits. The `--use-conda` flag must\nalso be set.")), ToolInput(tag="in_use_singularity", input_type=Boolean(optional=True), prefix="--use-singularity", doc=InputDocumentation(doc="If defined in the rule, run job within a singularity\ncontainer. If this flag is not set, the singularity\ndirective is ignored.")), ToolInput(tag="in_singularity_prefix", input_type=File(optional=True), prefix="--singularity-prefix", doc=InputDocumentation(doc="Specify a directory in which singularity images will\nbe stored.If not supplied, the value is set to the\n'.snakemake' directory relative to the invocation\ndirectory. If supplied, the `--use-singularity` flag\nmust also be set. The value may be given as a relative\npath, which will be extrapolated to the invocation\ndirectory, or as an absolute path.")), ToolInput(tag="in_singularity_args", input_type=String(optional=True), prefix="--singularity-args", doc=InputDocumentation(doc="Pass additional args to singularity.")), ToolInput(tag="in_use_env_modules", input_type=Boolean(optional=True), prefix="--use-envmodules", doc=InputDocumentation(doc="If defined in the rule, run job within the given\nenvironment modules, loaded in the given order. This\ncan be combined with --use-conda and --use-\nsingularity, which will then be only used as a\nfallback for rules which don't define environment\nmodules.\n")), ToolInput(tag="in_target", input_type=String(), position=0, doc=InputDocumentation(doc="Targets to build. May be rules or files.")), ToolInput(tag="in_disk_dot", input_type=String(), position=0, doc=InputDocumentation(doc="--cleanup-conda       Cleanup unused conda environments."))], outputs=[ToolOutput(tag="out_latency_wait", output_type=File(optional=True), selector=InputSelector(input_to_select="in_latency_wait", type_hint=File()), doc=OutputDocumentation(doc="Wait given seconds if an output file of a job is not\npresent after the job finished. This helps if your\nfilesystem suffers from latency (default 5).")), ToolOutput(tag="out_runtime_profile", output_type=File(optional=True), selector=InputSelector(input_to_select="in_runtime_profile", type_hint=File()), doc=OutputDocumentation(doc="Profile Snakemake and write the output to FILE. This\nrequires yappi to be installed."))], container="quay.io/biocontainers/snakelines:0.9.2--0", version="v0.1.0")


