Pod2Text_V0_1_0 = CommandToolBuilder(tool="pod2text", base_command=["pod2text"], inputs=[ToolInput(tag="in_alt", input_type=Boolean(optional=True), prefix="--alt", doc=InputDocumentation(doc="Use an alternate output format that, among other things, uses a\ndifferent heading style and marks '=item' entries with a colon in\nthe left margin.")), ToolInput(tag="in_code", input_type=Boolean(optional=True), prefix="--code", doc=InputDocumentation(doc="Include any non-POD text from the input file in the output as well.\nUseful for viewing code documented with POD blocks with the POD\nrendered and the code left intact.")), ToolInput(tag="in_color", input_type=Boolean(optional=True), prefix="--color", doc=InputDocumentation(doc="Format the output with ANSI color escape sequences. Using this\noption requires that Term::ANSIColor be installed on your system.")), ToolInput(tag="in_errors", input_type=String(optional=True), prefix="--errors", doc=InputDocumentation(doc="Set the error handling style. 'die' says to throw an exception on\nany POD formatting error. 'stderr' says to report errors on standard\nerror, but not to throw an exception. 'pod' says to include a POD\nERRORS section in the resulting documentation summarizing the\nerrors. 'none' ignores POD errors entirely, as much as possible.\nThe default is 'die'.")), ToolInput(tag="in_indent", input_type=Int(optional=True), prefix="--indent", doc=InputDocumentation(doc="Set the number of spaces to indent regular text, and the default\nindentation for '=over' blocks. Defaults to 4 spaces if this option\nisn't given.")), ToolInput(tag="in_loose", input_type=Boolean(optional=True), prefix="--loose", doc=InputDocumentation(doc="Print a blank line after a '=head1' heading. Normally, no blank line\nis printed after '=head1', although one is still printed after\n'=head2', because this is the expected formatting for manual pages;\nif you're formatting arbitrary text documents, using this option is\nrecommended.")), ToolInput(tag="in_left_margin", input_type=Int(optional=True), prefix="--left-margin", doc=InputDocumentation(doc="The width of the left margin in spaces. Defaults to 0. This is the\nmargin for all text, including headings, not the amount by which\nregular text is indented; for the latter, see -i option.")), ToolInput(tag="in_no_urls", input_type=Boolean(optional=True), prefix="--nourls", doc=InputDocumentation(doc="Normally, L<> formatting codes with a URL but anchor text are\nformatted to show both the anchor text and the URL. In other words:\nL<foo|http://example.com/>\nis formatted as:\nfoo <http://example.com/>\nThis flag, if given, suppresses the URL when anchor text is given,\nso this example would be formatted as just 'foo'. This can produce\nless cluttered output in cases where the URLs are not particularly\nimportant.")), ToolInput(tag="in_overstrike", input_type=File(optional=True), prefix="--overstrike", doc=InputDocumentation(doc="Format the output with overstrike printing. Bold text is rendered as\ncharacter, backspace, character. Italics and file names are rendered\nas underscore, backspace, character. Many pagers, such as less, know\nhow to convert this to bold or underlined text.")), ToolInput(tag="in_quotes", input_type=String(optional=True), prefix="--quotes", doc=InputDocumentation(doc="Sets the quote marks used to surround C<> text to quotes. If quotes\nis a single character, it is used as both the left and right quote.\nOtherwise, it is split in half, and the first half of the string is\nused as the left quote and the second is used as the right quote.\nquotes may also be set to the special value 'none', in which case no\nquote marks are added around C<> text.")), ToolInput(tag="in_sentence", input_type=Boolean(optional=True), prefix="--sentence", doc=InputDocumentation(doc="Assume each sentence ends with two spaces and try to preserve that\nspacing. Without this option, all consecutive whitespace in\nnon-verbatim paragraphs is compressed into a single space.")), ToolInput(tag="in_stderr", input_type=Boolean(optional=True), prefix="--stderr", doc=InputDocumentation(doc="By default, pod2text dies if any errors are detected in the POD\ninput. If --stderr is given and no --errors flag is present, errors\nare sent to standard error, but pod2text does not abort. This is\nequivalent to '--errors=stderr' and is supported for backward\ncompatibility.")), ToolInput(tag="in_termcap", input_type=File(optional=True), prefix="--termcap", doc=InputDocumentation(doc="Try to determine the width of the screen and the bold and underline\nsequences for the terminal from termcap, and use that information in\nformatting the output. Output will be wrapped at two columns less\nthan the width of your terminal device. Using this option requires\nthat your system have a termcap file somewhere where Term::Cap can\nfind it and requires that your system support termios. With this\noption, the output of pod2text will contain terminal control\nsequences for your current terminal type.")), ToolInput(tag="in_utf_eight", input_type=Boolean(optional=True), prefix="--utf8", doc=InputDocumentation(doc="By default, pod2text tries to use the same output encoding as its\ninput encoding (to be backward-compatible with older versions). This\noption says to instead force the output encoding to UTF-8.\nBe aware that, when using this option, the input encoding of your\nPOD source should be properly declared unless it's US-ASCII.\nPod::Simple will attempt to guess the encoding and may be successful\nif it's Latin-1 or UTF-8, but it will warn, which by default results\nin a pod2text failure. Use the '=encoding' command to declare the\nencoding. See perlpod(1) for more information.")), ToolInput(tag="in_width", input_type=Int(optional=True), prefix="--width", doc=InputDocumentation(doc="The column at which to wrap text on the right-hand side. Defaults to\n76, unless -t is given, in which case it's two columns less than the\nwidth of your terminal device.\n")), ToolInput(tag="in_ac_lost_u", input_type=Boolean(optional=True), prefix="-aclostu", doc=InputDocumentation(doc="")), ToolInput(tag="in_input", input_type=String(optional=True), position=0, doc=InputDocumentation(doc=""))], outputs=[ToolOutput(tag="out_overstrike", output_type=File(optional=True), selector=InputSelector(input_to_select="in_overstrike", type_hint=File()), doc=OutputDocumentation(doc="Format the output with overstrike printing. Bold text is rendered as\ncharacter, backspace, character. Italics and file names are rendered\nas underscore, backspace, character. Many pagers, such as less, know\nhow to convert this to bold or underlined text.")), ToolOutput(tag="out_termcap", output_type=File(optional=True), selector=InputSelector(input_to_select="in_termcap", type_hint=File()), doc=OutputDocumentation(doc="Try to determine the width of the screen and the bold and underline\nsequences for the terminal from termcap, and use that information in\nformatting the output. Output will be wrapped at two columns less\nthan the width of your terminal device. Using this option requires\nthat your system have a termcap file somewhere where Term::Cap can\nfind it and requires that your system support termios. With this\noption, the output of pod2text will contain terminal control\nsequences for your current terminal type."))], container=None, version="v0.1.0")


