_Remap_V0_1_0 = CommandToolBuilder(tool="_remap", base_command=["_remap"], inputs=[ToolInput(tag="in_enzymes", input_type=Boolean(optional=True), prefix="-enzymes", doc=InputDocumentation(doc="string     [all] The name 'all' reads in all enzyme\nnames from the REBASE database. You can\nspecify enzymes by giving their names with\ncommas between then, such as:\n'HincII,hinfI,ppiI,hindiii'.\nThe case of the names is not important. You\ncan specify a file of enzyme names to read\nin by giving the name of the file holding\nthe enzyme names with a '@' character in\nfront of it, for example, '@enz.list'.\nBlank lines and lines starting with a hash\ncharacter or '!' are ignored and all other\nlines are concatenated together with a comma\ncharacter ',' and then treated as the list\nof enzymes to search for.\nAn example of a file of enzyme names is:\n! my enzymes\nHincII, ppiII\n! other enzymes\nhindiii\nHinfI\nPpiI (Any string)")), ToolInput(tag="in_site_len", input_type=Boolean(optional=True), prefix="-sitelen", doc=InputDocumentation(doc="integer    [4] This sets the minimum length of the\nrestriction enzyme recognition site. Any\nenzymes with sites shorter than this will be\nignored. (Integer from 2 to 20)")), ToolInput(tag="in_min_cuts", input_type=Boolean(optional=True), prefix="-mincuts", doc=InputDocumentation(doc="integer    [1] This sets the minimum number of cuts for\nany restriction enzyme that will be\nconsidered. Any enzymes that cut fewer times\nthan this will be ignored. (Integer from 1\nto 1000)")), ToolInput(tag="in_max_cuts", input_type=Boolean(optional=True), prefix="-maxcuts", doc=InputDocumentation(doc="integer    [2000000000] This sets the maximum number of\ncuts for any restriction enzyme that will\nbe considered. Any enzymes that cut more\ntimes than this will be ignored. (Any\ninteger value)")), ToolInput(tag="in_single", input_type=Boolean(optional=True), prefix="-single", doc=InputDocumentation(doc="boolean    [N] If this is set then this forces the\nvalues of the mincuts and maxcuts qualifiers\nto both be 1. Any other value you may have\nset them to will be ignored.")), ToolInput(tag="in_plasmid", input_type=Boolean(optional=True), prefix="-plasmid", doc=InputDocumentation(doc="boolean    [N] If this is set then this allows searches\nfor restriction enzyme recognition site and\ncut positions that span the end of the\nsequence to be considered.")), ToolInput(tag="in_methylation", input_type=Boolean(optional=True), prefix="-methylation", doc=InputDocumentation(doc="boolean    [N] If this is set then RE recognition sites\nwill not match methylated bases.")), ToolInput(tag="in_table", input_type=Boolean(optional=True), prefix="-table", doc=InputDocumentation(doc="menu       [0] Genetic code to use (Values: 0\n(Standard); 1 (Standard (with alternative\ninitiation codons)); 2 (Vertebrate\nMitochondrial); 3 (Yeast Mitochondrial); 4\n(Mold, Protozoan, Coelenterate Mitochondrial\nand Mycoplasma/Spiroplasma); 5\n(Invertebrate Mitochondrial); 6 (Ciliate\nMacronuclear and Dasycladacean); 9\n(Echinoderm Mitochondrial); 10 (Euplotid\nNuclear); 11 (Bacterial); 12 (Alternative\nYeast Nuclear); 13 (Ascidian Mitochondrial);\n14 (Flatworm Mitochondrial); 15\n(Blepharisma Macronuclear); 16\n(Chlorophycean Mitochondrial); 21 (Trematode\nMitochondrial); 22 (Scenedesmus obliquus);\n23 (Thraustochytrium Mitochondrial))")), ToolInput(tag="in_frame", input_type=Boolean(optional=True), prefix="-frame", doc=InputDocumentation(doc="menu       [6] This allows you to specify the frames\nthat are translated. If you are not\ndisplaying cut sites on the reverse sense,\nthen the reverse sense translations will not\nbe displayed even if you have requested\nframes 4, 5 or 6. By default, all six frames\nwill be displayed. (Values: 1 (1); 2 (2); 3\n(3); F (Forward three frames); -1 (-1); -2\n(-2); -3 (-3); R (Reverse three frames); 6\n(All six frames))")), ToolInput(tag="in_flat_reformat", input_type=Boolean(optional=True), prefix="-flatreformat", doc=InputDocumentation(doc="boolean    [N] This changes the output format to one\nwhere the recognition site is indicated by a\nrow of '===' characters and the cut site is\npointed to by a '>' character in the\nforward sense, or a '<' in the reverse sense\nstrand.")), ToolInput(tag="in_m_file", input_type=Boolean(optional=True), prefix="-mfile", doc=InputDocumentation(doc="datafile   [Emethylsites.dat] Restriction enzyme\nmethylation data file")), ToolInput(tag="in_or_fm_in_size", input_type=Boolean(optional=True), prefix="-orfminsize", doc=InputDocumentation(doc="integer    [If this value is left as 0 then all of the\ntranslation is shown.] This sets the minimum\nsize of Open Reading Frames (ORFs) to\ndisplay in the translations. All other\ntranslation regions are masked by changing\nthe amino acids to '-' characters. (Integer\n0 or more)")), ToolInput(tag="in_uppercase", input_type=Boolean(optional=True), prefix="-uppercase", doc=InputDocumentation(doc="range      [If this is left blank, then the sequence\ncase is left alone.] Regions to put in\nuppercase.\nIf this is left blank, then the sequence\ncase is left alone.\nA set of regions is specified by a set of\npairs of positions.\nThe positions are integers.\nThey are separated by any non-digit,\nnon-alpha character.\nExamples of region specifications are:\n24-45, 56-78\n1:45, 67=99;765..888\n1,5,8,10,23,45,57,99")), ToolInput(tag="in_highlight", input_type=Boolean(optional=True), prefix="-highlight", doc=InputDocumentation(doc="range      [(full sequence)] Regions to colour if\nformatting for HTML.\nIf this is left blank, then the sequence is\nleft alone.\nA set of regions is specified by a set of\npairs of positions.\nThe positions are integers.\nThey are followed by any valid HTML font\ncolour.\nExamples of region specifications are:\n24-45 blue 56-78 orange\n1-100 green 120-156 red\nA file of ranges to colour (one range per\nline) can be specified as '@filename'.")), ToolInput(tag="in_three_letter", input_type=Boolean(optional=True), prefix="-threeletter", doc=InputDocumentation(doc="boolean    [N] Display protein sequences in\nthree-letter code")), ToolInput(tag="in_number", input_type=Boolean(optional=True), prefix="-number", doc=InputDocumentation(doc="boolean    [N] Number the sequences")), ToolInput(tag="in_width", input_type=Boolean(optional=True), prefix="-width", doc=InputDocumentation(doc="integer    [60] Width of sequence to display (Integer 1\nor more)")), ToolInput(tag="in_length", input_type=Boolean(optional=True), prefix="-length", doc=InputDocumentation(doc="integer    [0] Line length of page (0 for indefinite)\n(Integer 0 or more)")), ToolInput(tag="in_margin", input_type=Boolean(optional=True), prefix="-margin", doc=InputDocumentation(doc="integer    [10] Margin around sequence for numbering\n(Integer 0 or more)")), ToolInput(tag="in_offset", input_type=Boolean(optional=True), prefix="-offset", doc=InputDocumentation(doc="integer    [1] Offset to start numbering the sequence\nfrom (Any integer value)")), ToolInput(tag="in_html", input_type=Boolean(optional=True), prefix="-html", doc=InputDocumentation(doc="boolean    [N] Use HTML formatting")), ToolInput(tag="in_considered_dot", input_type=String(), position=0, doc=InputDocumentation(doc="-[no]ambiguity      boolean    [Y] This allows those enzymes which have one")), ToolInput(tag="in_the_mdot", input_type=String(), position=0, doc=InputDocumentation(doc="Advanced (Unprompted) qualifiers:"))], outputs=[], container=None, version="v0.1.0")


